  });

  return (
    <>
      <h1>TODO進捗管理</h1>
      <textarea />
      <button>+ TODOを追加</button>
      <h2>TODOリスト</h2>
      <ul>
        {todoList.map((todo) => (
          <li key={todo.id}>
            {todo.content}（{todo.done ? "完了" : "未完了"}）
          </li>
        ))}
      </ul>
    </>
  );
}
export default App;
